function qtyProduct() {
  $(".plus").click(function() {
    var e = $(this)
      .next()
      .find("input")
      .val();
    e++,
      $(this)
        .next()
        .find("input")
        .val(e),
      $(this)
        .closest(".slider-btm")
        .find(".cart-animation span")
        .html(e);
  }),
    $(".min").click(function() {
      var e = $(this)
        .prev()
        .find("input")
        .val();
      1 != e &&
        (e--,
        $(this)
          .prev()
          .find("input")
          .val(e),
        $(this)
          .closest(".slider-btm")
          .find(".cart-animation span")
          .html(e));
    });
}
