function filter() {
  $(".filter-title").click(function() {
    $(event.target)
      .closest(".filter")
      .toggleClass("active");
  }),
    $(".filter-btn").click(function() {
      $(".filters-block").addClass("active-filter"),
        $("body").css("overflow", "hidden");
    }),
    $(".filter-close").click(function() {
      $(".filters-block").removeClass("active-filter"),
        $("body").css("overflow", "auto");
    }),
    $(".more-filter").click(function() {
      "no" == $(this).attr("isOpen")
        ? ($(this).attr("isOpen", "yes"),
          $(this)
            .closest(".filter")
            .find(".filter-menu")
            .addClass("active"),
          $(this).text("დახურვა"))
        : ($(this).attr("isOpen", "no"),
          $(this)
            .closest(".filter")
            .find(".filter-menu")
            .removeClass("active"),
          $(this).text("მეტის ნახვა"));
    });
}
function priceRange() {
  var e = $("#min_value").val(),
    i = $("#max_value").val();
  $("#min_value").val(), $("#max_value").val();
  $("#slider-range").slider({
    range: !0,
    min: 0,
    max: 500,
    values: [e, i],
    slide: function(e, i) {
      $("#min_value").val(i.values[0]),
        $("#max_value").val(i.values[1]),
        $("#min_value").val(),
        $("#max_value").val();
    }
  });
  var s = $("#slider-range").slider("option", "max"),
    t = $("#slider-range").slider("option", "min");
  $("#min_value").val($("#slider-range").slider("values", 0)),
    $("#max_value").val($("#slider-range").slider("values", 1)),
    $("#min_value").change(function() {
      $(this).val() < t && $(this).val(t),
        $("#slider-range").slider("values", 0, $(this).val());
    }),
    $("#max_value").change(function() {
      $(this).val() > s && $(this).val(s),
        $("#slider-range").slider("values", 1, $(this).val());
    });
}
